import Container from 'react-bootstrap/Container';
import { useState } from 'react';

export default function AddProduct({showP, }){
    const [products, setProducts] = useState([]);
    const [prodInfo, setProdInfo] = useState({
        prodName: '',
        prodPrice: 0,
        prodStocks: 0,
      })
      console.log(products)
    return(
        <Container style={{ display: showP ? "block" : "none", border: '1px solid black' }}>
             Name: 
             <input 
                value={prodInfo.prodName}
                onChange={e => setProdInfo({...prodInfo, prodName: e.target.value})}
                
            /><br/>
            Price: 
            <input 
                value={prodInfo.prodPrice}
                onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
            /><br/>
            Stock:
            <input 
                value={prodInfo.prodStocks}
                onChange={e => setProdInfo({...prodInfo, prodStocks: e.target.value})}
            /><br/>
            <button onClick={() => setProducts({...products, prodInfo})}>Add</button>
            <table>
          <thead>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
            <th>Action</th>
          </thead>
          <tbody>
           
            {products.map(prod => (
              prod.id === toEdit ?
              
              <>
                <tr key={prod.id}>
              <td>{prod.id}</td>
              <td><input
              placeholder={prod.name} 
                value={newName}
                onChange={e => setNewName(e.target.value)}
              /></td>
              <td><input
              placeholder={prod.price} 
                value={newPrice}
                onChange={e => setNewPrice(e.target.value)}
              /></td>
              <td><input
              placeholder={prod.stock} 
                value={newStock}
                onChange={e => setNewStock(e.target.value)}
              /></td>
              <td>
                <select
                  value={categ}
                  onChange={e => setCateg(e.target.value)}
                >
                    <option value=''>--Select Category--</option>
                    {category.map(cat => (
                    <option value={cat}>{cat}</option>
                    ))}
               </select>
              </td>
              <td><button onClick={
                () => EditQ(prod.id)
              }> Save Edits</button></td>
              </tr>
              </>
              :
              <tr key={prod.id}>
              <td>{prod.id}</td>
              <td>{prod.name}</td>
              <td>{prod.price}</td>
              <td>{prod.stock}</td>
              <td>{prod.categ}</td>
              <td ><button onClick={() => setToEdit(prod.id)}>Edit </button><br/>
              <button onClick={() => Del(prod.id)}>
                  Delete Product ID = {prod.id}
              </button></td>
              </tr>
            ))}
          </tbody>
        </table>
        </Container>
        
    )
}
