import { useState } from 'react';
import { useEffect } from 'react';
import Container from 'react-bootstrap/Container';


export default function Categ({showP, }){
    const [categName, setCategName ] = useState('') 
   
    return(
        <Container style={{ display: showP ? "block" : "none" }}>
            Category: <input
                value={categName}
                onChange={e => {
                    setCategName(e.target.value)
                }}/>
                <button onClick={
                    useEffect(()=> {
                        setCategList([...categList, categName]);
                       },[])
                }>Add</button>
            
        </Container>
        
    )
}
