import { useState } from 'react';
import { useEffect } from 'react';
import Container from 'react-bootstrap/Container';

export default function Categ({showP, categList, setCategList}){
    const [categName, setCategName ] = useState('') 
    return(
        <Container style={{ display: showP ? "block" : "none", border: '1px solid black' }}>
            Category: <input
                value={categName}
                onChange={e => {
                    setCategName(e.target.value)
                    useEffect(()=> {
                        setCategList([...categList, e.target.value]);
                       },[]);
                }}
            />
        </Container>
        
    )
}
