import { useState } from "react";
export default function App() {
  const [show, setShow] = useState(true);
  return (
    <div className="App">
      <Box status={show} />
      <button onClick={() => setShow(!show)}>
        {show ? "Hide" : "Show"} component
      </button>
    </div>
  );
}

function Box(props) {
  return (
    <div
      style={{ visibility: props.status ? "visible" : "hidden", border: '1px solid black' }}
      className="box"
    >AWWW</div>
  );
}
