import Container from 'react-bootstrap/Container';
import { useState } from 'react';
import Table from 'react-bootstrap/Table';

export default function AddProduct({showP, categList}){
    const [products, setProducts] = useState([]);
    const [prodInfo, setProdInfo] = useState({
        prodID : 0,
        prodName: '',
        prodPrice: 0,
        prodStocks: 0,
        category: 0
      })
      const [prodToEdit, setProdToEdit] = useState('');
    function handleEdit(id){
        setProdToEdit(id)
    }
    function Del(id){
        setProdToEdit(id)
        SaveEdit(id)
    }
    function SaveEdit(id){
        const updatedProducts = products.map(product => {
            if (product.id === id) {
              product.prodName = prodInfo.prodName
              product.prodPrice = prodInfo.prodPrice
              product.prodStock = prodInfo.prodStocks
              product.category = prodInfo.category
            }
            return product
          });
        
          setProducts([...updatedProducts])
    }

    console.log(products)
    return(
        <Container style={{ display: showP ? "block" : "none", border: '1px solid black' }}>
             ID: 
             <input 
                value={prodInfo.prodID+1}
                readOnly
            /><br/>
            Name: 
             <input 
                value={prodInfo.prodName}
                onChange={e => 
                   setProdInfo({...prodInfo, prodName: e.target.value})
                    
            }
                
            /><br/>
            Price: 
            <input 
                type='number'
                value={prodInfo.prodPrice}
                onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
            /><br/>
            Stock:
            <input 
                type='number'
                value={prodInfo.prodStocks}
                onChange={e => setProdInfo({...prodInfo, prodStocks: e.target.value})}
            /><br/>
            <select
                  value={prodInfo.category}
                  onChange={e => setProdInfo({...prodInfo, category: parseInt(e.target.value)})}
                >
                    <option value=''>--Select Category--</option>
                    {categList.map((item, index) => (
                    <option value={item[0]}>{item[1]}</option>
                    ))}
               </select>
            <button onClick={() => 
                prodInfo.prodName === '' ? alert("Enter Name") : prodInfo.category === '' ? alert('Enter Category') :
                [
                    setProdInfo({...prodInfo, prodID: prodInfo.prodID+=1}),
                    setProducts([...products, prodInfo]),
                    setProdInfo({
                        prodID : 0,
                        prodName: '',
                        prodPrice: 0,
                        prodStocks: 0,
                        category: 0
                    })
                ]
                
            
            }>Add</button>
            
        <Table responsive striped="columns">
            <thead >
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Category</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
            {products.map(prod => 
                prodToEdit === prod.prodID ?
                (
                    <tr>
                    <td>
                        <input 
                            value={prodInfo.prodID+1}
                            readOnly
                        />
                    </td>
                    <td>
                        <input 
                            value={prodInfo.prodName}
                            onChange={e => 
                            setProdInfo({...prodInfo, prodName: e.target.value})}
                        />
                    </td>
                    <td>
                        <input 
                            type='number'
                            value={prodInfo.prodPrice}
                            onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
                        />    
                    </td>
                    <td>
                        <input 
                            type='number'
                            value={prodInfo.prodPrice}
                            onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
                        />
                    </td>
                    <td>
                        <input 
                            type='number'
                            value={prodInfo.prodStocks}
                            onChange={e => setProdInfo({...prodInfo, prodStocks: e.target.value})}
                        />
                    </td>
                    {
                    categList.map((items,index)=>
                      items[0] === prod.category ?
                      (
                      <select
                        value={prodInfo.category}
                        onChange={e => setProdInfo({...prodInfo, category: parseInt(e.target.value)})}
                      >
                          <option value=''>--Select Category--</option>
                          {categList.map((item, index) => (
                          <option value={item[0]}>{item[1]}</option>
                          ))}
                     </select>
                     )
                      :
                      <></>
                    )}
                    <td>
                    {prodToEdit === prod.prodID ? (
                          <button onClick={() => SaveEdit(prod.prodID)}>Save</button>
                        ) : (
                          <>
                            <button onClick={() => handleEdit(prod.prodID)}>Edit</button>
                            <button onClick={() => Del(prod.prodID)}>Delete</button>
                          </>
                        )}
                    </td>
                    </tr>
                  )
                  :
                  (
                    <tr>
                    <td>{prod.prodID}</td>
                    <td>{prod.prodName}</td>
                    <td>{prod.prodPrice}</td>
                    <td>{prod.prodStocks}</td>
                    {
                    categList.map((items,index)=>
                      items[0] === prod.category ?
                      <td>{items[1]}</td>
                      :
                      <></>
                    )}
                    <td>
                    {prodToEdit === prod.prodID ? (
                          <button onClick={() => SaveEdit(prod.prodID)}>Save</button>
                        ) : (
                          <>
                            <button onClick={() => handleEdit(prod.prodID)}>Edit</button>
                            <button onClick={() => Del(prod.prodID)}>Delete</button>
                          </>
                        )}
                    </td>
                    </tr>
                  )
                  
            )}
            </tbody>
        </Table>
        </Container>
        
    )
}
