import { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import AddProduct from './addProduct';
import Categ from './addCateg'
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
export default function CollapsibleExample() {
  const [show, setShow] = useState([{
    home: false,
    products: true,
    category: false,
    POS: false
  }])
  const [catList, setCatList] = useState([]);
  const handleCatList = (arrayFromAddCateg) => {
    setCatList(arrayFromAddCateg);
  };
  return (
    <>
    <Navbar collapseOnSelect expand="lg" className="bg-body-tertiary">
      <Container>
        <Navbar.Brand href="#" onClick={() => setShow({home: false, products: false,category: false,POS: false})}>React-Bootstrap</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
            {/* <Nav.Link href="#" onClick={() => setShow({home: true, products: true,category: false,POS: false})}>Add Products</Nav.Link>
            <Nav.Link href="#"onClick={() => setShow({home: true, products: false,category: true,POS: false})}>Add Category</Nav.Link> */}
          </Nav>
          <Nav>
            <Nav.Link href="#">More deets</Nav.Link>
            <Nav.Link eventKey={2} href="#">
              Dank memes
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    <AddProduct showP={show.products} categList={catList}/>
    <Categ showP={show.category} onSendArrayToApp={handleCatList}/>
    <Container style={{display: show.home ? 'none' : 'block'}}>
      <h1 >Welcome</h1>
    </Container>

    <Tabs
              defaultActiveKey="profile"
              id="uncontrolled-tab-example"
              className="mb-3"
            >
            <Tab eventKey="home" title="Home">
              Tab content for Home
            </Tab>
            <Tab eventKey="profile" title="Profile">
              Tab content for Profile
            </Tab>
            <Tab eventKey="contact" title="Contact" disabled>
              Tab content for Contact
            </Tab>
    </Tabs>


    </>
  );
}
