import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';

export default function PoS({ prodList }) {
    const [products, setProducts] = useState([]);
    const [prodInfo, setProdInfo] = useState({
        prodID : 0,
        prodName: '',
        prodPrice: 0,
        prodStocks: 0,
        category: 0
      })


    return (
        <Container className="mt-3 border border-info">
            <div className='row '>
                <div className='col-4'>
                ID: 
             <input 
                value={prodInfo.prodID+1}
                readOnly
            /><br/>
            Name: 
             <input 
                value={prodInfo.prodName}
                onChange={e => 
                   setProdInfo({...prodInfo, prodName: e.target.value})
                    
            }
                
            /><br/>
            Price: 
            <input 
                type='number'
                value={prodInfo.prodPrice}
                onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
            /><br/>
                </div>
                <div className='col-8'>
                ID: 
             <input 
                value={prodInfo.prodID+1}
                readOnly
            /><br/>
            Name: 
             <input 
                value={prodInfo.prodName}
                onChange={e => 
                   setProdInfo({...prodInfo, prodName: e.target.value})
                    
            }
                
            /><br/>
            Price: 
            <input 
                type='number'
                value={prodInfo.prodPrice}
                onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
            /><br/>
                </div>
            </div>
        </Container>
    );
}
