export default function ChildComponent({ showP, onSendArrayToParent }) {
    const [catName, setCatName] = useState('')
    const [arrayToSend, setArrayToSend] = useState([]); // Array to be sent to the parent
  
    function sendArrayToParent() {
      // Call the callback function from props and pass the array as an argument
      setArrayToSend([...arrayToSend, catName])
      
    };
    onSendArrayToParent(arrayToSend);
    
    return (
        <Container style={{ display: showP ? "block" : "none" }}>
       Name: 
             <input 
                value={catName}
                onChange={e => 
                   setCatName(e.target.value)
                    
            }
                
            /><br/>
        <button onClick={sendArrayToParent}>Send Array to Parent</button>
        </Container>
    );
  }
