import Container from 'react-bootstrap/Container';
import { useState } from 'react';
import Table from 'react-bootstrap/Table';

export default function AddProduct({showP, }){
    const nextId = 0
    
    const [products, setProducts] = useState([]);
    const [prodInfo, setProdInfo] = useState({
        prodID : '',
        prodName: '',
        prodPrice: 0,
        prodStocks: 0,
      })
      prodInfo.prodID = ('prodKey-'+nextId)
    return(
        <Container style={{ display: showP ? "block" : "none", border: '1px solid black' }}>
             ID: 
             <input 
                value={prodInfo.prodID}
                onChange={e => setProdInfo({...prodInfo, prodID: e.target.value})}
                
            /><br/>
            Name: 
             <input 
                value={prodInfo.prodName}
                onChange={e => setProdInfo({...prodInfo, prodName: e.target.value})}
                
            /><br/>
            Price: 
            <input 
                value={prodInfo.prodPrice}
                onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
            /><br/>
            Stock:
            <input 
                value={prodInfo.prodStocks}
                onChange={e => setProdInfo({...prodInfo, prodStocks: e.target.value})}
            /><br/>
            <button onClick={() => setProducts([...products, prodInfo])}>Add</button>
            <table>
          <thead>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
          </thead>
          <tbody>
           
            {products.map(prod => (
              <tr>
              <td>{prod.prodName}</td>
              <td>{prod.prodPrice}</td>
              <td>{prod.prodStocks}</td>
              {/* <td ><button onClick={() => setToEdit(prod.id)}>Edit </button><br/>
              <button onClick={() => Del(prod.id)}>
                  Delete Product ID = {prod.id}
              </button></td> */}
              </tr>
            ))}
          </tbody>
        </table>
        <Table responsive striped="columns">
            <thead >
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
            {products.map(prod => (
              <tr>
              <td>{prod.prodID}</td>
              <td>{prod.prodName}</td>
              <td>{prod.prodPrice}</td>
              <td>{prod.prodStocks}</td>
              {/* <td ><button onClick={() => setToEdit(prod.id)}>Edit </button><br/>
              <button onClick={() => Del(prod.id)}>
                  Delete Product ID = {prod.id}
              </button></td> */}
              </tr>
            ))}
            </tbody>
        </Table>
        </Container>
        
    )
}
