import { useState } from 'react';
import { useEffect } from 'react';
import Container from 'react-bootstrap/Container';


export default function Categ({showP, onSendArrayToParent }){
    const [categName, setCategName ] = useState('') 
    const [categList, setCategList] = useState([])

    const sendArrayToParent = () => {
        // Call the callback function from props and pass the array as an argument
        setCategList([...categList, categName])
        onSendArrayToParent(categList);
      };
    return(
        <Container style={{ display: showP ? "block" : "none" }}>
            Category: <input
                value={categName}
                onChange={e => {
                    setCategName([...categName, e.target.value])
                }}/>
                <button onClick={sendArrayToParent}>Add</button>
            
        </Container>
        
    )
}
