import { useState } from 'react';
import { useEffect } from 'react';
import Container from 'react-bootstrap/Container';

export default function Categ({showP, categList, setCategList}){
    const [categName, setCategName ] = useState('') 
    function AddCat(){
        useEffect(()=> {
            setCategList([...categList, categName]);
           },[]);
    }
    return(
        <Container style={{ display: showP ? "block" : "none", border: '1px solid black' }}>
            Category: <input
                value={categName}
                onChange={e => {
                    setCategName(e.target.value)
                }}/>
                <button onClick={() => AddCat()}>Add</button>
            
        </Container>
        
    )
}
