export default function ChildComponent({ onSendArrayToParent }) {
    const arrayToSend = [1, 2, 3, 4, 5]; // Array to be sent to the parent
  
    const sendArrayToParent = () => {
      // Call the callback function from props and pass the array as an argument
      onSendArrayToParent(arrayToSend);
    };
  
    return (
      <div>
        <button onClick={sendArrayToParent}>Send Array to Parent</button>
      </div>
    );
