                                            <th>Item Name</th>
                                            <th>Stock</th>
                                            <th>Category</th>
                                            <th>Total</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {cart.map(cart => (
                                            cart.name === name ?
                                                <tr>
                                                    <td></td>
                                                    {
                                                        prodList.map(prod => {
                                                            if (cart.prodID === prod.prodID) {
                                                                return (
                                                                    <>
                                                                        <td>{prod.prodName}</td>
                                                                        <td>{cart.stock}</td>

                                                                        {
                                                                            categList.map((item, index) => {
                                                                                if (prod.category === item[0]) {
                                                                                    return (<td>{item[1]}</td>)
                                                                                }
                                                                                return null;
                                                                            })
                                                                        }
                                                                    </>
                                                                )
                                                            }
                                                            return null;
                                                        })
                                                    }
                                                    <td>{cart.total}</td>
                                                </tr>
                                                :
                                                <></>
                                        ))}
                                    </tbody>
                                </Table>
                            </Col>
                        )
                }
                <Col>
                    <Row xs={2} md={3}>
                        {/* aw */}
                        {prodList.map((prod, index) => (
                            prod.prodStocks <= 0 ?
                                <></>
                                :
                                (<Col key={index} className="mb-2">
                                    <Card style={{ width: '15rem' }}>
                                        <Card.Header>{prod.prodName}</Card.Header>
                                        <Card.Body>
                                            <Card.Text>
                                                <p>Price: {prod.prodPrice}</p>
                                                <p>Stock: {prod.prodStocks}</p>
                                                {
                                                    categList.map((item, index) => {
                                                        if (prod.category === item[0]) {
                                                            return (<p>Category: {item[1]}</p>)
                                                        }
                                                        return null;
                                                    })
                                                }
                                            </Card.Text>
                                        </Card.Body>
                                        {user.name === ''
                                            ?
                                            <></>
                                            :
                                            <Card.Footer>
                                                <input
                                                    type="number"
                                                    value={productStocks[prod.prodID] || ''}
                                                    onChange={e => {
                                                        setProductStocks({ ...productStocks, [prod.prodID]: e.target.value });
                                                    }}
                                                />
                                                <button onClick={() => addToCart(prod.prodID)}>Order</button>
                                            </Card.Footer>
                                        }
                                    </Card>
                                </Col>)
                        ))}
                    </Row>
                </Col>
            </Row>
        </Container>
    );
}
