import Container from 'react-bootstrap/Container';
import { useState } from "react";
import Table from 'react-bootstrap/Table';


export default function ChildComponent({ showP, onSendArrayToApp }) {
    const [catName, setCatName] = useState('')
    const [arrayToSend, setArrayToSend] = useState([]); // Array to be sent to the App
    const [id, setId] = useState(0)
    function SaveCateg() {
        setId
        setArrayToSend([...arrayToSend, [nextId.id+=1, catName]])
        setCatName('')
    };
    // function from parameter
    onSendArrayToApp(arrayToSend);
    
    return (
        <Container style={{ display: showP ? "block" : "none" }}>
            Name: 
                <input 
                    value={catName}
                    onChange={e => 
                    setCatName(e.target.value)
                        
                }
                        
                /><br/>
                <button onClick={SaveCateg}>Send Array to Parent</button>
                <Table responsive striped="columns">
                    <thead >
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                    {arrayToSend.map((item, index) => (
                    <tr>
                    <td>{item[0]}</td>
                    <td>{item[1]}</td>
                    {/* <td ><button onClick={() => setToEdit(prod.id)}>Edit </button><br/>
                    <button onClick={() => Del(prod.id)}>
                        Delete Product ID = {prod.id}
                    </button></td> */}
                    </tr>
                    ))}
                    </tbody>
                </Table>
        </Container>
    );
  }
