import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';


export default function PoS({ prodList, categList }) {
    const [products, setProducts] = useState([]);
    const [prodInfo, setProdInfo] = useState({
        prodID : 0,
        prodName: '',
        prodPrice: 0,
        prodStocks: 0,
        category: 0
      })


    return (
        <Container className="mt-3 border border-info">
            <Row className=''>
                <Col md={4} className='d-flex flex-column justify-content-center'>
                ID: 
                <input 
                    value={prodInfo.prodID+1}
                    readOnly
                /><br/>
                Name: 
                <input 
                    value={prodInfo.prodName}
                    onChange={e => 
                    setProdInfo({...prodInfo, prodName: e.target.value})
                        
                }
                    
                /><br/>
                Price: 
                <input 
                    type='number'
                    value={prodInfo.prodPrice}
                    onChange={e => setProdInfo({...prodInfo, prodPrice: e.target.value})}
                /><br/>
                </Col>
                <Col>
                <Table responsive striped="columns">
                    <thead >
                        <tr>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Stock</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            prodList.map(prod=>(
                                <tr>
                                <td>{prod.prodName}</td>
                                <td>{prod.prodPrice}</td>
                                <td>{prod.prodStocks}</td>
                                {
                                categList.map((items,index)=>
                                items[0] === prod.category ?
                                <td>{items[1]}</td>
                                :
                                <></>
                                )}
                                </tr>
                            ))
                        }
                    </tbody>
                </Table>
                </Col>
            </Row>
        </Container>
    );
}
