import Container from 'react-bootstrap/Container';
import { useState } from "react";
import Table from 'react-bootstrap/Table';


export default function ChildComponent({ showP, onSendArrayToApp }) {
    const [catToEdit, setCatToEdit] = useState();
    const [catName, setCatName] = useState('')
    const [arrayToSend, setArrayToSend] = useState([]); // Array to be sent to the App
    const [id, setId] = useState(0)

    function SaveCateg() {
        setId(prevId => prevId + 1)
        setArrayToSend([...arrayToSend, [id, catName]])
        setCatName('')
    };
    // function from parameter
    onSendArrayToApp(arrayToSend);

    
    return (
        <Container style={{ display: showP ? "block" : "none" }}>
            Name: 
                <input 
                    value={catName}
                    onChange={e => 
                    setCatName(e.target.value)
                        
                }
                        
                /><br/>
                <button onClick={SaveCateg}>Send Array to Parent</button>
                <Table responsive striped="columns">
                    <thead >
                        <tr>
                            <th>index in array</th>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        catToEdit === index ? 
                            <>
                                <input 
                    value={catName}
                    onChange={e => 
                    setCatName(e.target.value)
                        
                    
                }             
                                /><br/>
                                <button onClick={SaveCateg}>Send Array to Parent</button>
                            </>
                        :
                        arrayToSend.map((item, index) => (
                        <tr>
                        <td>{index}</td>
                        <td>{item[0]}</td>
                        <td>{item[1]}</td>
                        <td ><button onClick={() => setCatToEdit(index)}>Edit </button><br/>
                        <button onClick={() => Del(index)}>
                            Delete Product ID = {index}
                        </button></td>
                        </tr>
                        ))}
                    </tbody>
                </Table>
        </Container>
    );
  }
