import React, { useState }  from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Table from 'react-bootstrap/esm/Table';

export default function PoS({ prodList, categList, setProdList,  onSendArrayToProd }) {
    
    useEffect(() => {
        onSendArrayToProd(prodList);
      }, [prodList, onSendArrayToProd]);

    const [name, setName] = useState('')
    const [user, setUser] = useState({
        name: ''
    })
    const [cart, setCart] = useState([])
    const [transaction, setTransaction] = useState([])
    const [productStocks, setProductStocks] = useState({})
    console.log(transaction)
    function handleTransaction(){
        const transactionTemp = cart.filter(cart => cart.name === name)
        setTransaction(transactionTemp)
        const tempProdList = prodList.map(prod=>{
            if(prod.prodID === transaction.prodID){
                prod.prodSTocks = prod.prodSTocks - transaction.stock
            }
            return prod
        })
        setProdList(tempProdList)








        const updatedCart = cart.filter(cart => cart.name !== name)
        setCart(updatedCart)
    }
    function addToCart(id) {
        const stockValue = productStocks[id] || 0;
        setCart([...cart, { name: name, prodID: id, stock: stockValue }])
        setProductStocks({ ...productStocks, [id]: '' })
    }

    return (
        <Container className="mt-3">
            <Row className=''>
                
                    {
                        user.name === '' 
                        ? 
                        (
                        <Col md={4} >
                        <Form>
                            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control value={name} onChange={e=>setName(e.target.value)} />
                            </Form.Group>
                                <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                                <button onClick={()=>{
                                    setUser({...user, name: name})
                                    }}>Save</button>
                            </Form.Group>
                        </Form>
                        </Col>
                        
                        )
                        : 
                        (
                        <Col md={5}>
                            <div className='d-flex justify-content-between mb-4'>
                                <h3>Welcome {user.name}!</h3>
                                <button onClick={()=>{
                                     setUser({ name: ''})
                                }}>Log Out</button>
                            </div>
                            <Table>
                                <thead>
                                    <tr>
                                        <th>Your Cart</th>
                                        <th>Item Name</th>
                                        <th>Stock</th>
                                        <th>Category</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {cart.map(cart=>(
                                      cart.name === name ? 
                                      <tr>
                                        <td></td>
                                        {
                                            prodList.map(prod=>{
                                                if(cart.prodID === prod.prodID){
                                                    return(
                                                        <>
                                                            <td>{prod.prodName}</td>
                                                            <td>{cart.stock}</td>

                                                           {
                                                            categList.map((item, index)=>{
                                                                if(prod.category === item[0]){
                                                                    return (<td>{item[1]}</td>)
                                                                }
                                                                return null;
                                                            })
                                                            }
                                                        </>
                                                    )
                                                }
                                                return null;
                                            })
                                            }
                                      </tr>  
                                      :
                                      <></>
                                    ))}
                                </tbody>
                            </Table>
                            <button onClick={()=>handleTransaction()}>Buy now</button>
                        </Col>
                        )
                    }
                <Col>
                <Row xs={2} md={3}>
                    {/* aw */}
                        {prodList.map((prod, index) => (
                            prod.prodStocks === 0 ? 
                            <></>
                            :
                            (<Col key={index} className="mb-2">
                            <Card style={{ width: '15rem' }}>
                                <Card.Header>{prod.prodName}</Card.Header>
                                <Card.Body>
                                    <Card.Text>
                                        <p>Price: {prod.prodPrice}</p>
                                        <p>Stock: {prod.prodStocks}</p>
                                        {
                                        categList.map((item, index)=>{
                                            if(prod.category === item[0]){
                                                return (<p>Category: {item[1]}</p>)
                                            }
                                            return null;
                                        })
                                        }
                                    </Card.Text>
                                </Card.Body>
                                    {user.name === '' 
                                    ?
                                    <></> 
                                    :
                                    <Card.Footer>
                                        <input
                                            type="number"
                                            value={productStocks[prod.prodID] || ''}
                                            onChange={e =>{
                                                setProductStocks({ ...productStocks, [prod.prodID]: e.target.value });
                                            }}
                                        />
                                        <button onClick={() => addToCart(prod.prodID)}>Order</button>
                                    </Card.Footer>
                                    }
                            </Card>
                        </Col>)
                        ))}
                    </Row>
                </Col>
            </Row>
        </Container>
    );
}
